import { useRef, useEffect, useState } from 'react';
import './App.css';
import * as faceapi from "face-api.js";
import { FaceExpressions } from 'face-api.js';
import * as React from 'react';
// import express from 'express';

function App() {

  let number = 0;

  const videoRef = useRef();
  const canvasRef = useRef();

  useEffect(() => {
    startVideo();

    videoRef && loadModels();

  }, []);

  const loadModels = () => {
    Promise.all([
      faceapi.nets.tinyFaceDetector.loadFromUri('/models'),
      faceapi.nets.faceLandmark68Net.loadFromUri('/models'),
      faceapi.nets.faceRecognitionNet.loadFromUri('/models'),
      faceapi.nets.faceExpressionNet.loadFromUri('/models'),
    ]).then(() => {
      faceDetection();
    })
  };

  const startVideo = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((currentStream) => {
        videoRef.current.srcObject = currentStream;
      })
      .catch((err) => {
        console.error(err)
      });
  }

  const faceDetection = async () => {
    setInterval(async() => {
      if (number==1)
      {
        return
      }
      // withFaceLandmarks().

      const detections = await faceapi.detectAllFaces(videoRef.current, new faceapi.TinyFaceDetectorOptions()).withFaceExpressions();
      let emotions_displayed = detections[0].expressions;
      if (emotions_displayed != undefined)
      {
        number++;

      }
      let max_emotion = 0
      let final_emotion = ""
      for (const [key,value] of Object.entries(emotions_displayed))
      {
        if (value > max_emotion)
        {
          max_emotion = value
          final_emotion = key
        }
      }

      const options = {
        method: 'GET',
        headers: {
          'X-RapidAPI-Key': 'fb5b0920f6msh804c07d430a8210p1d0067jsn6dc2b529beba',
          'X-RapidAPI-Host': 'spotify23.p.rapidapi.com'
        }
      };

      fetch('https://spotify23.p.rapidapi.com/search/?q=' + final_emotion + '&type=playlist&limit=5', options)
        .then(response => response.json())
        .then( (val) => {
          const selected_playlist = val.playlists.items[0].data['uri'];
          const selected_playlist_id = selected_playlist.slice(17);
          console.log(selected_playlist.slice(17));
          const playlist_opened = 'https://open.spotify.com/playlist/' + selected_playlist_id;
          // console.log(typeof selected_playlist);
          window.open(playlist_opened,'_blank');
        } )
        // .then(response => console.log(response))
        .catch(err => console.error(err));

      canvasRef.current.innerHtml = faceapi.createCanvasFromMedia(videoRef.current);
      faceapi.matchDimensions(canvasRef.current, {
        width: 940,
        height: 650,
      })

      const resized = faceapi.resizeResults(detections, {
        width: 940,
        height: 650,
      });

      faceapi.draw.drawDetections(canvasRef.current, resized)
      faceapi.draw.drawFaceLandmarks(canvasRef.current, resized)
      faceapi.draw.drawFaceExpressions(canvasRef.current, resized)

    }, 3000)
  }

  return (
    <div  className="app">
      <h1> AI FACE DETECTION</h1>
      <h1>  </h1>
      <div className='app__video'>
        <video crossOrigin='anonymous' ref={videoRef} autoPlay ></video>
      </div>
        <canvas ref={canvasRef} width="940" height="650" className='app__canvas' />
    </div>
  );
}

export default App;
