import { useRef, useEffect } from 'react';
import './App.css';
import * as faceapi from "face-api.js";
import { FaceExpressions } from 'face-api.js';
import $ from 'jquery';
// new faceapi.TinyFaceDetectorOptions()

function App() {
  let number = 0;
  const videoRef = useRef();
  const canvasRef = useRef();

  useEffect(() => {
    startVideo();

    videoRef && loadModels();

  }, []);

    const loadModels = () => {
      Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri('/models'),
        faceapi.nets.faceLandmark68Net.loadFromUri('/models'),
        faceapi.nets.faceRecognitionNet.loadFromUri('/models'),
        faceapi.nets.faceExpressionNet.loadFromUri('/models'),
      ]).then(() => {
        faceDetection();
      })
    };

  const startVideo = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((currentStream) => {
        videoRef.current.srcObject = currentStream;
      })
      .catch((err) => {
        console.error(err)
      });
  }

  const faceDetection = async () => {
    setInterval(async() => {
      if (number==1)
      {
        return
      }
      const detections = await faceapi.detectAllFaces(videoRef.current, new faceapi.TinyFaceDetectorOptions()).withFaceLandmarks().withFaceExpressions();
      let emotions_displayed = detections[0].expressions;
      if (emotions_displayed != undefined)
      {
        number++;
      }
      let max_emotion = 0
      let final_emotion = ""
      for (const [key,value] of Object.entries(emotions_displayed))
      {
        if (value > max_emotion)
        {
          max_emotion = value
          final_emotion = key
        }
      }
      console.log(final_emotion + ':' + max_emotion)

      canvasRef.current.innerHtml = faceapi.createCanvasFromMedia(videoRef.current);
      faceapi.matchDimensions(canvasRef.current, {
        width: 940,
        height: 650,
      })

      const resized = faceapi.resizeResults(detections, {
        width: 940,
        height: 650,
      });

      faceapi.draw.drawDetections(canvasRef.current, resized)
      faceapi.draw.drawFaceLandmarks(canvasRef.current, resized)
      faceapi.draw.drawFaceExpressions(canvasRef.current, resized)

      let number_of_playlists = 5
      let selected_playlist = Math.floor(Math.random() * number_of_playlists)

      $.get("https://api.spotify.com/v1/search?q=" + final_emotion + "&type=track&limit=5", function(data) {
                                console.log(data)
                                playlist = data[selected_playlist].external_urls.spotify;
                                // playlist = data.playlists.items[selected_playlist].external_urls.spotify;
                                window.open(playlist, '_blank');
                            })

      // fetch("https://api.spotify.com/v1/search?q=" + final_emotion + "&type=playlist&limit=5")

    }, 1000)
  }

  return (
    <div  className="app">
      <h1> AI FACE DETECTION</h1>
      <h1>  </h1>
      <div className='app__video'>
        <video crossOrigin='anonymous' ref={videoRef} autoPlay ></video>
      </div>
        <canvas ref={canvasRef} width="940" height="650" className='app__canvas' />

    </div>
  );
}

export default App;
